public class HelloJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
		HelloJava.say("Hi~");
		
		// JVM이 스스로 화면에 출력
		System.out.println("Hello Java!!");
		 
		
	}

	public static void say(String msg) {
		System.out.println(msg);
	}
}